import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [alerts, setAlerts] = useState([]);
  const [filter, setFilter] = useState('all');
  const [expandedAlert, setExpandedAlert] = useState(null);
  const [stats, setStats] = useState({ high: 0, medium: 0, low: 0, info: 0 });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAlerts();
    const interval = setInterval(fetchAlerts, 5000);
    return () => clearInterval(interval);
  }, []);

  const fetchAlerts = async () => {
    try {
      const res = await fetch('/api/alerts');
      const data = await res.json();
      setAlerts(data);
      calculateStats(data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching alerts:', err);
    }
  };

  const calculateStats = (alertList) => {
    const counts = { high: 0, medium: 0, low: 0, info: 0 };
    alertList.forEach(a => {
      if (counts.hasOwnProperty(a.severity)) {
        counts[a.severity]++;
      }
    });
    setStats(counts);
  };

  const filteredAlerts = alerts.filter(a => filter === 'all' || a.severity === filter);

  const getSeverityColor = (severity) => {
    const colors = {
      critical: '#ff1744',
      high: '#ff6b6b',
      medium: '#ffa502',
      low: '#ffd93d',
      info: '#6bcf7f'
    };
    return colors[severity] || '#999';
  };

  const getSeverityBgColor = (severity) => {
    const colors = {
      critical: 'rgba(255, 23, 68, 0.1)',
      high: 'rgba(255, 107, 107, 0.1)',
      medium: 'rgba(255, 165, 2, 0.1)',
      low: 'rgba(255, 217, 61, 0.1)',
      info: 'rgba(107, 207, 127, 0.1)'
    };
    return colors[severity] || 'rgba(0, 0, 0, 0.05)';
  };

  const markResolved = (id) => {
    setAlerts(prev => prev.map(a => a.alert_id === id ? { ...a, status: 'resolved' } : a));
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', minHeight: '100vh', color: '#fff' }}>
      <div style={{ padding: 32 }}>
        <h1 style={{ textAlign: 'center', fontSize: '2.5em', marginBottom: 20, textShadow: '2px 2px 4px rgba(0,0,0,0.5)' }}>ðŸš¨ AI Intrusion & Anomaly Detector Dashboard</h1>

        {/* Risk Score Visualization */}
        <div style={{ marginBottom: 32, background: 'rgba(255,255,255,0.1)', padding: 20, borderRadius: 10 }}>
          <h2>Risk Score Distribution</h2>
          <div style={{ display: 'flex', justifyContent: 'space-around' }}>
            {Object.entries(severityCounts).map(([severity, count]) => (
              <div key={severity} style={{ textAlign: 'center' }}>
                <div style={{
                  width: 50,
                  height: count * 20,
                  background: severity === 'high' ? '#ff6b6b' : severity === 'medium' ? '#ffd93d' : '#6bcf7f',
                  margin: '0 auto',
                  borderRadius: 5,
                  transition: 'height 0.5s'
                }}></div>
                <p>{severity}: {count}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Filter */}
        <div style={{ marginBottom: 20 }}>
          <label>Filter by Severity: </label>
          <select value={filter} onChange={e => setFilter(e.target.value)} style={{ padding: 5, borderRadius: 5 }}>
            <option value="all">All</option>
            <option value="high">High</option>
            <option value="medium">Medium</option>
            <option value="low">Low</option>
          </select>
        </div>

        {/* Alerts Table */}
        <div style={{ background: 'rgba(255,255,255,0.1)', padding: 20, borderRadius: 10, overflowX: 'auto' }}>
          <h2>Alerts ({filteredAlerts.length})</h2>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ background: 'rgba(255,255,255,0.2)' }}>
                <th style={{ padding: 10 }}>Alert ID</th>
                <th>User</th>
                <th>Severity</th>
                <th>Score</th>
                <th>Reasons</th>
                <th>Status</th>
                <th>Action</th>
                <th>Resolve</th>
              </tr>
            </thead>
            <tbody>
              {filteredAlerts.map(a => (
                <tr key={a.alert_id} style={{
                  background: a.severity === 'high' ? 'rgba(255,108,107,0.3)' : a.severity === 'medium' ? 'rgba(255,217,61,0.3)' : 'rgba(107,207,127,0.3)',
                  animation: 'fadeIn 0.5s'
                }}>
                  <td style={{ padding: 10 }}>{a.alert_id}</td>
                  <td>{a.username}</td>
                  <td>{a.severity}</td>
                  <td>{a.score.toFixed(2)}</td>
                  <td>{a.reasons.join(', ')}</td>
                  <td>{a.status}</td>
                  <td>{a.action || '-'}</td>
                  <td><button onClick={() => markResolved(a.alert_id)} style={{ padding: 5, borderRadius: 5, background: '#4CAF50', color: '#fff', border: 'none' }}>Resolve</button></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <style>{`
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        table th, table td {
          border: 1px solid rgba(255,255,255,0.3);
        }
      `}</style>
    </div>
  );
}

export default App;
